void cepstrum_vec(DATA *audio, DATA *out){
    
    DATA to_fft[512];
    DATA to_mel[256];
    LDATA to_dct[24];
    DATA to_log[24];
    DATA to_std_norm[16];
    
    LDATA maxn;

    unsigned int i = 0;
    
    for(i = 0; i < 512; i++){
        
        to_fft[i] = (i < 400)? audio[i]: 0; //vector para fft
    }
    
    rfft(to_fft, 512, SCALE); //desbordamiento controll
    to_fft[1] = 0;

    fft_norm(to_fft, to_mel, 512);
    
    // Rect LPF at ~6kHz
    for (i = 193; i < 256; i++) {
    	to_mel[i]  = (0);
    }

    mmul(to_mel, 1, 256, mel_filter_bank, 256, 24, to_log);
    
    for (i = 20; i < 24; i++) {
        	if (to_log[i]  == 0)
        		to_log[i] = 1;
        }

    if(logn(to_log, to_dct, 24))
    	printf("logn overflow\n");

    for(i = 0; i < 24; i++){
    	to_dct[i] = ((to_dct[i] + 340700)>>4);
    }

    mmul((DATA*)to_dct, 1, 24, dct_mat, 24, 16, to_std_norm);


    std_norm(to_std_norm, out);//we keep 16 first dct coeficients
    
}

void fft_norm(DATA *fftC, DATA *abs_fft, unsigned int fftsize){
    
    int i;
    unsigned long int temp1, temp2;
    unsigned int index;
    unsigned int info;
    DATA a,b;
    
    for (i = 0; i < fftsize; i += 2){
        a = *(fftC + i);
        b = *(fftC + i  +1);
        temp1 = (unsigned long int)*(fftC + i) * *(fftC + i);
        temp2 = (unsigned long int)*(fftC + i + 1) * *(fftC + i + 1);
        index = (i >> 1);
        info = sqrt32(temp1 + temp2);
        *(abs_fft + index) = info;
    }

}


signed int sqrt32(unsigned long int num){
    
    signed long int dist = 0;
    signed long int dist_check = 0;
    signed int estimation = 0;
    signed int last_est = 0;
    unsigned int i = 0;
    
    
    do{
        
        estimation |= (1 << (14 - i));
        
        dist = ((signed long int)estimation * estimation - num);
        
        if (dist == 0)
            return estimation;
        
        if (dist > 0){
            estimation = last_est;
        }
        
        else{
            last_est = estimation;
        }
        i++;
            }while(i <= 14);
    
    dist_check = ((signed long int)(estimation+1) * (estimation+1) - num);
    dist = ((signed long int)estimation * estimation - num);
    
    if( abs(dist_check) < abs(dist) )
        return (estimation+1);
    else if ( abs(dist_check = ((signed long int)(estimation-1) * (estimation-1) - num)) < abs(dist) )
        return (estimation - 1);
    else
        return estimation;
}

void std_norm(DATA *vec, DATA *out){
    //suponemos vecsize 16
    unsigned int i;
    signed int std;
    signed long int mean = 0;
    signed long long int temp1 = 0;
    
    for( i = 0; i < 16; i++){
        mean += *(vec + i);
    }
     printf("%ld\n",mean);
    //20|15
    mean >>= 4;//16|15
    printf("%ld\n",mean);
    for( i = 0; i < 16; i++){
        temp1 += ((signed long long int)(*(vec+i) - mean) * (*(vec+i) - mean) );
        
    }//35|30
    temp1 >>= 4; //31/30
    
    std = sqrt32((unsigned long int) temp1); //16|15
    
    for(i = 0; i < 16; i++){
         *(out + i) = (DATA)( ((LDATA)*(vec + i) << 3) / std);
    }
}


